import react,{us} from 'react'
import useFireStore from '../Hooks/useFireStore'
import {motion} from 'framer-motion';


const ImageGrid=({setSelectedImg,setCurrentImgIndex})=>{
  const {docs} =useFireStore('images');
  const [imageDocs, setImageDocs] = useState([]); // Define a state variable for 'docs'

  // Use 'useEffect' to update 'imageDocs' when 'docs' is available
  useEffect(() => {
    if (docs) {
      setImageDocs(docs);
    }
  }, [docs]);

  console.log(imageDocs);

  return(
    <div className="img-grid">
        { docs && docs.map(doc=>(
            <motion.div className="imag-wrap" 
            key={doc.id}
            layout
            whileHover={{opacity:1}}
            onClick={()=>{
              setSelectedImg(doc.url)
              setCurrentImgIndex(docs.indexOf(doc))
            }

          }
            >

            <motion.img src={doc.url} 
            alt="uploaded file"
            initial={{opacity:0}}
            animate={{opacity:1}}
            />
            </motion.div> 
        ))}
        </div>
  )
}


export default ImageGrid;
