import React from 'react'
import {motion} from 'framer-motion'
import {
    MDBCarousel,
    MDBCarouselInner,
    MDBCarouselItem,
    MDBCarouselElement
  } from 'mdb-react-ui-kit';

const Modal = ({selectedimg,setSelectedImg}) => {

  const handleClick=(e)=>{
      if(e.target.classList.contains('backdrop'))
     setSelectedImg(null);
  }
    return (
        <motion.div className="backdrop" 
        onClick={handleClick}
        initial={{opacity:0}}
        animate={{opacity:1}}>
        <MDBCarousel showIndicators showControls fade>
      <MDBCarouselInner>
        <MDBCarouselItem className='active'>
          <MDBCarouselElement src={selectedimg} 
            alt="anlarged pic"  />
            </MDBCarouselItem>
            </MDBCarouselInner>
    </MDBCarousel>
    </motion.div>


    )
}

export default Modal
