import React, { useState, useEffect } from 'react';

const Modal = ({ selectedImg, setSelectedImg, docs, currentImgIndex, setCurrentImgIndex }) => {
  const [nextDisabled, setNextDisabled] = useState(false);
  const [prevDisabled, setPrevDisabled] = useState(false);

  useEffect(() => {
    // Disable "Previous" button if on the first image
    setPrevDisabled(currentImgIndex === 0);

    // Disable "Next" button if on the last image
    setNextDisabled(currentImgIndex === docs.length - 1);
  }, [currentImgIndex, docs]);

  const handleNext = () => {
    const nextIndex = currentImgIndex + 1;
    if (nextIndex < docs.length) {
      setSelectedImg(docs[nextIndex].url);
      setCurrentImgIndex(nextIndex);
    }
  };

  const handlePrevious = () => {
    const previousIndex = currentImgIndex - 1;
    if (previousIndex >= 0) {
      setSelectedImg(docs[previousIndex].url);
      setCurrentImgIndex(previousIndex);
    }
  };

  return (
    <div className="modal">
      <img src={selectedImg} alt="enlarged pic" />
      <button onClick={handlePrevious} disabled={prevDisabled}>Previous</button>
      <button onClick={handleNext} disabled={nextDisabled}>Next</button>
    </div>
  );
};

export default Modal;
